attrs.script:
cat <<EOF > /usr/share/nginx/html/config/mysqlConfig.js
var mysql = require('mysql');

var mysqlConnection = mysql.createConnection({
   host: "@@{DB_IP_ADDRESS}@@",
   user: "@@{DB_USERNAME}@@",
   password: "@@{DB_PASSWORD}@@",
   database: "@@{DB_NAME}@@"
});

mysqlConnection.connect(function(err) {
    if (err) throw err;
});

module.exports = mysqlConnection;

EOF

sed -i 's/NODE_SERVER_IP_ADDRESS/@@{address}@@/g' /usr/share/nginx/html/js/data.js

